USE DClub;
GO
CREATE TABLE Users
(
	Id INT PRIMARY KEY IDENTITY(1,1),
	FirstName NVARCHAR(50) NOT NULL,
	LastName NVARCHAR(50) NOT NULL,
	MiddleName NVARCHAR(50) NOT NULL,
	BirthDate DATETIME NOT NULL,
	Email NVARCHAR(100) NOT NULL UNIQUE,
	Phone NVARCHAR(50),
	RegistrationDate DATETIME NOT NULL,
	[Password] NVARCHAR(100),
	City NVARCHAR(100) NOT NULL,
	[Address] NVARCHAR(200),
	Avatar VARBINARY(MAX),
	AvatarMimeType NVARCHAR(20),
	Sex INT -- 1 - male, 2 - female
)


CREATE TABLE Roles
(
	Id INT PRIMARY KEY IDENTITY(1,1),
	Name NVARCHAR(50)
)

CREATE TABLE UsersRoles
(
	IdUsersRoles INT PRIMARY KEY IDENTITY(1,1),
	IdUser INT FOREIGN KEY REFERENCES Users(Id) ON DELETE CASCADE ON UPDATE CASCADE NOT NULL,
	IdRole INT FOREIGN KEY REFERENCES Roles(Id) ON DELETE CASCADE ON UPDATE CASCADE	NOT NULL
)



CREATE TABLE DogTypes
(
	Id INT PRIMARY KEY IDENTITY(1,1),
	Name NVARCHAR(50)
)


CREATE TABLE Dogs
(
	Id INT PRIMARY KEY IDENTITY(1,1),
	Name NVARCHAR(50) NOT NULL,
	Age INT NOT NULL,
	[Description] NVARCHAR(200),
	Avatar VARBINARY(MAX),
	AvatarMimeType NVARCHAR(20),
	OwnerId INT FOREIGN KEY REFERENCES Users(Id) ON DELETE CASCADE ON UPDATE CASCADE NOT NULL,
	WasSold bit,
	Sex INT,
	IdDogType INT FOREIGN KEY REFERENCES DogTypes(Id) ON DELETE CASCADE ON UPDATE CASCADE NOT NULL,
)


CREATE TABLE DogsPhotos
(
	Id INT PRIMARY KEY IDENTITY(1,1),
	PhotoData VARBINARY(MAX),
	MimeType NVARCHAR(20),
	DogId INT FOREIGN KEY REFERENCES Dogs(Id) ON DELETE CASCADE ON UPDATE CASCADE NOT NULL
)

CREATE TABLE Avards
(
	Id INT PRIMARY KEY IDENTITY(1,1),
	Name NVARCHAR(100) NOT NULL,
	[Description] NVARCHAR(200) NOT NULL,
)

--выставки
CREATE TABLE Expositions
(
	Id INT PRIMARY KEY IDENTITY(1,1),
	Name NVARCHAR(100) NOT NULL,
	DateStart DATETIME NOT NULL,
	[Description] NVARCHAR(200),
)

CREATE TABLE ExpositionMembers
(
	Id INT PRIMARY KEY IDENTITY (1,1),
	IdExposition INT FOREIGN KEY REFERENCES Expositions(Id) ON DELETE CASCADE ON UPDATE CASCADE NOT NULL,
	IdDog INT FOREIGN KEY REFERENCES Dogs(Id) ON DELETE CASCADE ON UPDATE CASCADE NOT NULL
)

CREATE TABLE ExpositionWinners
(
	Id INT PRIMARY KEY IDENTITY(1,1),
	IdExpositionMember INT FOREIGN KEY REFERENCES ExpositionMembers(Id) ON DELETE CASCADE ON UPDATE CASCADE NOT NULL,
	IdAvard INT FOREIGN KEY REFERENCES Avards(Id) ON DELETE CASCADE ON UPDATE CASCADE NOT NULL,
	WinnerPlace INT NOT NULL
)

--заявка на участие
CREATE TABLE ExpositionsRequests
(
	Id INT PRIMARY KEY IDENTITY(1,1),
	IdExposition INT FOREIGN KEY REFERENCES Expositions(Id) ON DELETE CASCADE ON UPDATE CASCADE NOT NULL,
	IdDog INT FOREIGN KEY REFERENCES Dogs(Id) ON DELETE CASCADE ON UPDATE CASCADE NOT NULL
)

CREATE TABLE Vaccinations
(
	Id INT PRIMARY KEY IDENTITY(1,1),
	Name NVARCHAR(50),
	DateVacination DATETIME,
	IdDog INT FOREIGN KEY REFERENCES Dogs(Id) ON DELETE CASCADE ON UPDATE CASCADE NOT NULL
)

CREATE TABLE MedicalCares
(
	Id INT PRIMARY KEY IDENTITY(1,1),
	Name NVARCHAR(50),
	DateCare DATETIME,
	[Description] NVARCHAR(500),
	IdDog INT FOREIGN KEY REFERENCES Dogs(Id) ON DELETE CASCADE ON UPDATE CASCADE NOT NULL
)

CREATE TABLE PaymentsPeriodTypes
(
	Id INT PRIMARY KEY IDENTITY(1,1),
	Name NVARCHAR(500) NOT NULL
)

CREATE TABLE PaymentTypes
(
	Id INT PRIMARY KEY IDENTITY(1,1),
	Name NVARCHAR(100) NOT NULL,
	IdPeriod INT FOREIGN KEY REFERENCES PaymentsPeriodTypes(Id) ON DELETE CASCADE ON UPDATE CASCADE NOT NULL
)

CREATE TABLE Payments
(
	Id INT PRIMARY KEY IDENTITY(1,1),
	PaySize DECIMAL(18,2) NOT NULL,
	PayDate DATETIME NOT NULL,
	[Description] NVARCHAR(300),
	UserId INT FOREIGN KEY REFERENCES Users(Id) ON DELETE CASCADE ON UPDATE CASCADE NOT NULL ,
	IdType INT FOREIGN KEY REFERENCES PaymentTypes(Id) ON DELETE CASCADE ON UPDATE CASCADE NOT NULL,
)

CREATE TABLE DogsForSale
(
	Id INT PRIMARY KEY IDENTITY(1,1),
	Price DECIMAL(18,2),
	ClubCommission DECIMAL(18,2),
	IdDog INT FOREIGN KEY REFERENCES Dogs(Id) ON DELETE CASCADE ON UPDATE CASCADE NOT NULL,
)

CREATE TABLE SalesHistory
(
	Id INT PRIMARY KEY IDENTITY(1,1),
	IdDog INT FOREIGN KEY REFERENCES Dogs(Id) NOT NULL,
	OldOwnerId INT FOREIGN KEY REFERENCES Users(Id)  NOT NULL,
	NewOwnerId INT FOREIGN KEY REFERENCES Users(Id)  NOT NULL, -----------------
	CostPrice DECIMAL(18,2) NOT NULL,
	SaleDate DATETIME NOT NULL,
	ClubCommission DECIMAL (18,2) NOT NULL
)

CREATE TABLE News
(
	Id INT PRIMARY KEY IDENTITY(1,1),
	Title NVARCHAR(200),
	NewsDate DATETIME,
	NewsText NVARCHAR(2000)
)

